mapper.select:
SELECT	MAX(order_no) 
FROM 	board 
WHERE 	group_no = #{groupNo}

SELECT board.no AS no, title, name, read_count AS readCount, reg_date AS regDate, group_no AS groupNo, order_no AS orderNo, depth, user_no AS userNo, board_delete AS boardDelete, @rownum := @rownum + 1 AS RNUM, parent_no AS parentNo FROM board, (select @rownum :=0) AS R, users WHERE users.no = board.user_no
AND title LIKE #{word}
ORDER BY group_no DESC, order_no DESC LIMIT #{startPageIndex}, 5

mapper.update:
UPDATE  board 
SET 	title = #{title}, 
		content = #{content} 
WHERE 	no = #{no}

UPDATE  board 
SET 	read_count = read_count + 1 
WHERE 	no = #{no}

UPDATE board 
SET    board_delete = 1 
where  no = #{no}


	UPDATE board 
	SET    order_no = order_no + 1 
	WHERE  group_no = #{groupNo} 
	AND    order_no >= #{orderNo}
